package holygradleimport org.gradle.*import org.gradle.api.*class StatedPrerequisite {    public final String name    private final PrerequisitesChecker checker    public final def params = null    private boolean hasRun = false    private boolean ok = false        StatedPrerequisite(PrerequisitesChecker checker) {        this.name = checker.name        this.checker = checker    }        StatedPrerequisite(PrerequisitesChecker checker, def params) {        this.name = checker.name        this.checker = checker        this.params = params    }        public void check() {        if (!hasRun) {            hasRun = true            if (params == null) {                ok = checker.run()            } else {                ok = checker.run(params)            }        }    }        public boolean checkWithoutFailure() {        if (!hasRun) {            if (params == null) {                println "Checking '${name}'..."                ok = checker.runWithoutFailure()            } else {                println "Checking '${name}' (${params.join(', ')})..."                ok = checker.runWithoutFailure(params)            }            hasRun = true        }        ok    }}