package holygradleimport org.gradle.*import org.gradle.api.*class StatedPrerequisite {    public final String name    private final PrerequisitesChecker checker    public final def params    private boolean hasRun = false    private boolean ok = false        StatedPrerequisite(PrerequisitesChecker checker, def params) {        this.name = checker.name        this.checker = checker        this.params = params    }        public void check() {        if (!hasRun) {            hasRun = true            checker.run(params)        }    }        public boolean checkWithoutFailure() {        if (!hasRun) {            if (params == null) {                println "Checking '${name}'..."            } else {                println "Checking '${name}' ($params)..."            }            hasRun = true            ok = checker.runWithoutFailure(params)        }        ok    }}